function inputFocus(e){let t=document.getElementById(e),i=document.getElementById(e+"Validation");t.value.length>0?(t.classList.add("is-active"),t.classList.remove("is-error"),i.classList.remove("is-error")):t.classList.remove("is-active")}function validateForm(){let e=document.getElementById("inputName"),t=document.getElementById("inputNameValidation"),i=document.getElementById("inputEmail"),s=document.getElementById("inputEmailValidation");e.value.length<=0&&(e.classList.add("is-error"),t.classList.add("is-error")),i.value.length<=0&&(i.classList.add("is-error"),s.classList.add("is-error"))}